import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getApplication, submitApplication, uploadAttachment, downloadAttachment } from '../api';

const ApplicationDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [application, setApplication] = useState(null);
  const [loading, setLoading] = useState(true);
  const [uploading, setUploading] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    fetchApplication();
  }, [id]);

  const fetchApplication = async () => {
    try {
      const res = await getApplication(id);
      setApplication(res.data);
    } catch (err) {
      console.error(err);
      alert('Application not found');
      navigate('/');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async () => {
    try {
      await submitApplication(id);
      fetchApplication(); // refresh
    } catch (err) {
      alert('Failed to submit');
    }
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) return;
    setUploading(true);
    try {
      await uploadAttachment(id, selectedFile);
      setSelectedFile(null);
      fetchApplication(); // refresh attachments
    } catch (err) {
      alert('Upload failed');
    } finally {
      setUploading(false);
    }
  };

  const handleDownload = async (attachmentId, filename) => {
    try {
      const res = await downloadAttachment(id, attachmentId);
      const url = window.URL.createObjectURL(new Blob([res.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', filename);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      alert('Download failed');
    }
  };

  if (loading) return <div>Loading...</div>;
  if (!application) return <div>Application not found</div>;

  return (
    <div>
      <h2>Application Detail</h2>
      <p><strong>Status:</strong> {application.status}</p>
      <p><strong>Submitted:</strong> {application.submitted_at ? new Date(application.submitted_at).toLocaleString() : 'Not submitted'}</p>
      <h3>Application Data</h3>
      <pre>{JSON.stringify(application.data, null, 2)}</pre>

      {application.status === 'draft' && (
        <div>
          <button onClick={handleSubmit} className="btn">Submit Application</button>
        </div>
      )}

      <h3>Attachments</h3>
      {application.attachments && application.attachments.length > 0 ? (
        <ul>
          {application.attachments.map(att => (
            <li key={att.id}>
              {att.filename} (uploaded {new Date(att.uploaded_at).toLocaleString()})
              <button onClick={() => handleDownload(att.id, att.filename)} className="btn">Download</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No attachments yet.</p>
      )}

      {application.status === 'draft' && (
        <div>
          <h4>Upload New Attachment</h4>
          <input type="file" onChange={handleFileChange} />
          <button onClick={handleUpload} disabled={!selectedFile || uploading} className="btn">
            {uploading ? 'Uploading...' : 'Upload'}
          </button>
        </div>
      )}
    </div>
  );
};

export default ApplicationDetail;
