import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createApplication } from '../api';

const ApplicationForm = () => {
  const [formData, setFormData] = useState({});
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await createApplication(formData);
      navigate(`/applications/${res.data.id}`);
    } catch (err) {
      console.error(err);
      alert('Failed to create application');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>New Application</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Position</label>
          <input type="text" name="position" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Experience (years)</label>
          <input type="number" name="experience" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Cover Letter</label>
          <textarea name="coverLetter" rows="5" onChange={handleChange} required></textarea>
        </div>
        <button type="submit" className="btn" disabled={loading}>
          {loading ? 'Creating...' : 'Create Draft'}
        </button>
      </form>
    </div>
  );
};

export default ApplicationForm;
